import React, { useEffect } from "react";
import { Spring } from "react-spring/renderprops";

export default function MotorPageHeader({
  manifesto_Arr,
  nameHeaderTitle,
  titleSize,
  colorBg,
  contentHeaderContent,
  price,
  bgHeaderParallax,
  reveredContent,
}) {
  useEffect(() => {
    if (manifesto_Arr.length < 5) {
      for (var i = 0; i < manifesto_Arr.length; i++) {
        document.getElementsByClassName("motorPageHeader-manifesto")[
          i
        ].style.height = 101.8 + "px";
      }
    }else{
      for (var k = 0; k < manifesto_Arr.length; k++) {
        document.getElementsByClassName("motorPageHeader-manifesto")[
          k
          ].style.height = (1 / manifesto_Arr.length) * 100 + "%";
          document.getElementsByClassName("motorPageHeader-manifesto")[
          k
          ].style.marginTop= 5+"px";
          document.getElementsByClassName("motorPageHeader-manifesto")[k].style.fontSize= "98%";
      }
    }
  });

  return (
    <>
      <div
        className="motorPageHeader"
        style={{
          // backgroundImage: "url(./img/dragster800rrscs/dragster-800-rr-xl.png)",
          background: `${colorBg}`,
          position: "relative",
        }}
      >
        <div
          className="motorPageHeader-Content"
          style={{ zIndex: 10, left: reveredContent === true ? 0 : null }}
        >
          <Spring
            from={{
              opacity: 0,
              transition: "0.4s ease",
              backgroundImage: "url(./img/" + nameHeaderTitle + ")",
              backgroundSize: titleSize,
            }}
            to={{
              opacity: 1,
              backgroundImage: "url(./img/" + nameHeaderTitle + ")",
              backgroundSize: titleSize,
            }}
          >
            {(props) => (
              <div
                className="motorPageHeader-Content-top"
                style={
                  props
                  // backgroundImage: "url(./img/" + nameHeaderTitle + ")"
                }
              ></div>
            )}
          </Spring>
          <div className="motorPageHeader-Content-center">
            <div className="motorPageHeader-Content-center-top">
              {manifesto_Arr.map((item, index) => (
                <Spring
                  from={{
                    opacity: 0,
                    position: "relative",
                    top: 100,
                    // transition: "0.25s",
                  }}
                  to={{
                    opacity: 1,
                    top: 0,
                  }}
                >
                  {(props) => (
                    <div className="motorPageHeader-manifesto">
                      <p style={props}>{manifesto_Arr[index]}</p>
                    </div>
                  )}
                </Spring>
              ))}
            </div>
            <Spring
              from={{ opacity: 0, transition: "0.4s ease" }}
              to={{ opacity: 1 }}
            >
              {(props) => (
                <div
                  className="motorPageHeader-Content-center-bottom"
                  style={props}
                >
                  <p>{contentHeaderContent}</p>
                </div>
              )}
            </Spring>
          </div>
          <Spring
            from={{ opacity: 0, transition: "0.4s ease" }}
            to={{ opacity: 1 }}
          >
            {(props) => (
              <div className="motorPageHeader-Content-bottom" style={props}>
                <p>Starting from {price} â‚¬</p>
              </div>
            )}
          </Spring>
        </div>
        <div
          className="xxxxx"
          style={{
            backgroundImage: `url(./img/${bgHeaderParallax})`,
            width: "100%",
            height: "100vh",
            position: "sticky",
            top: 0,
            backgroundPosition: "top",
            backgroundSize: "cover",
          }}
        ></div>
      </div>
    </>
  );
}
