import React from "react";

export default function Motor3d({ imgLocate, nameModel, price }) {
  var check = 0;
  var oldX = 0;
  var oldX_mobile = 0;
  async function renderModel3d() {
    for (var i = 0; i < 36; i++) {
      var newDiv = document.createElement("img");
      newDiv.setAttribute("src", "./img/" + imgLocate + "_" + i + ".png");
      newDiv.setAttribute("width", "100%");
      newDiv.setAttribute("class", "dr-img");
      newDiv.setAttribute("id", i);
      var drContainer = document.getElementById("dr-3ds");
      drContainer.appendChild(newDiv);
    }
    await moveImg();
  }
  async function moveImg() {
    document.getElementById(0).style.opacity = "1";
    function mouseMove(event, y) {
      var x = event.pageX;
      // var o= event.clientX;
      if (x > oldX) {
        check++;
        if (check > 35) {
          check = 0;
        }
      } else if (x < oldX) {
        check--;
        if (check < 0) {
          check = 35;
        }
      }
      for (var i = 0; i < 36; i++) {
        document.getElementById(i).style.opacity = "0";
      }
      document.getElementById(check).style.opacity = "1";
      // document.getElementById("xxx").innerHTML =
      //   "true " + x + " " + y + " " + check;
      console.log("new = " + x + ", " + oldX);
      oldX = event.pageX;
    }
    function touchMove(event, y) {
      var x = event.pageX;
      // var o= event.clientX;
      var o = event.changedTouches[0].pageX;
      if (x > oldX || o > oldX_mobile) {
        check++;
        if (check > 35) {
          check = 0;
        }
      } else if (x < oldX || o < oldX_mobile) {
        check--;
        if (check < 0) {
          check = 35;
        }
      }
      for (var i = 0; i < 36; i++) {
        document.getElementById(i).style.opacity = "0";
      }
      document.getElementById(check).style.opacity = "1";
      oldX = event.pageX;
      oldX_mobile = event.changedTouches[0].pageX;
    }
    document
      .getElementById("dr-3ds")
      .addEventListener("mousedown", function (event) {
        var y = event.pageX;
        document.getElementById("dr-3ds").onmousemove = function (event) {
          mouseMove(event, y);
        };
      });
    document
      .getElementById("dr-3ds")
      .addEventListener("mouseup", function (event) {
        document.getElementById("dr-3ds").onmousemove = null;
      });
    document
      .getElementById("dr-3ds")
      .addEventListener("touchstart", function (event) {
        var y = event.pageX;
        document.getElementById("dr-3ds").ontouchmove = function (event) {
          touchMove(event, y);
        };
      });
    document
      .getElementById("dr-3ds")
      .addEventListener("touchend", function (event) {
        document.getElementById("dr-3ds").ontouchmove = null;
      });
  }
  // function autoMove(){
  //   setInterval(()=>{
  //           check++;
  //           if(check!=36){
  //               for (var i = 0; i < 36; i++) {
  //                   document.getElementById(i).style.opacity = "0";
  //               }
  //               document.getElementById(check).style.opacity = "1";
  //           }else{
  //               check = 0;
  //               for (var i = 0; i < 36; i++) {
  //                   document.getElementById(i).style.opacity = "0";
  //               }
  //               document.getElementById(check).style.opacity = "1";
  //           }
  //       }, 35);
  // }
  window.onload = () => {
    renderModel3d();
    // autoMove();
  };
  return (
    <>
      <div className="dr-container" id="dr-container">
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            background: "aliceblue",
            height: "20%",
            zIndex: 100
          }}
        ></div>
        <div className="dr-3ds" id="dr-3ds" style={{width: "100%", height: "100%", position: "relative"}}></div>
        <div
          style={{
            position: "absolute",
            bottom: 0,
            left: 0,
            width: "100%",
            background: "aliceblue",
            height: "20%",
            zIndex: 100
          }}
        ></div>
        {/* <div className="model3d-name">
          <div className="model3d-inf-top">
            <h4>{nameModel}</h4>
          </div>
          <div className="model3d-inf-bottom">
            <button className="model3d-button">Price: {price} â‚¬</button>
          </div>
        </div> */}
      </div>
    </>
  );
}
